import 'package:flutter/material.dart';import 'package:web_socket_channel/web_socket_channel.dart';class ObsController extends StatefulWidget {  const ObsController({super.key});  @override  State<ObsController> createState() => _ObsControllerState();}class _ObsControllerState extends State<ObsController> {  WebSocketChannel? channel;  final _serverUrlController = TextEditingController();  void _connectToObs(String url) {    channel = WebSocketChannel.connect(Uri.parse(url));  }  void _sendStartRecordingCommand() {    final command = {      "op": 6,      "d": {"requestType": "StartRecording", "requestId": "123"}    };    channel?.sink.add(command.toString());  }  @override  void dispose() {    _serverUrlController.dispose();    channel?.sink.close();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('OBS Controller'),      ),      body: Column(        children: [          TextField(            controller: _serverUrlController,            decoration: const InputDecoration(labelText: 'OBS WebSocket URL'),          ),          ElevatedButton(            onPressed: () => _connectToObs(_serverUrlController.text),            child: const Text('Connect'),          ),          ElevatedButton(            onPressed: _sendStartRecordingCommand,            child: const Text('Start Recording'),          ),        ],      ),    );  }}